package org.horizon.sdk.library.java.tool.lang.validation.violation;

import lombok.AllArgsConstructor;
import lombok.Getter;
import org.horizon.sdk.library.java.tool.lang.validation.support.ValidationSchema;

/**
 * represents a single validation failure record containing field-specific error information.
 *
 * <p>this class is typically generated by the validation process and collected in a {@link Violator}.
 * each instance corresponds to one failed validation rule on a specific field.</p>
 *
 * @author wjm
 * @since 2025-04-19 01:40
 */
@Getter
@AllArgsConstructor
public class Violation {

    /**
     * name of the field that failed validation.
     * <ul>
     *   <li>matches the field name configured in {@link ValidationSchema}</li>
     *   <li>used to identify the problematic field in error messages</li>
     * </ul>
     */
    private String fieldName;

    /**
     * formatted error message describing the validation failure.
     * <p>message characteristics:
     * <ul>
     *   <li>pre-formatted with field name as first parameter</li>
     *   <li>messages come from {@link ViolationMessageType}</li>
     *   <li>may contain placeholders like {} for dynamic values</li>
     * </ul>
     *
     * <p>example message:
     * <pre>"field 'age' must be greater than 18"</pre>
     */
    private String message;

}